					if($("#creditbank").val() == "" || $("#creditbank").siblings('input').val() == '') {
						return _this.popup("请选择信用卡银行");
					}
					if($("#debitbank").val() == "" || $("#debitbank").siblings('input').val() == '') {
						return _this.popup("请选择储蓄卡银行");
					}
